SELECT BEN_ID, mco, count(mco) as valueocmco, ADD_REGION, count(ADD_REGION) as valueocaddr 
into #mcotable
FROM [SUP_03].[dbo].[2020_ALCOHOL] WHERE CDE_CLM_TYPE in ('M', 'O', 'I') 
group by BEN_ID, mco, add_region ;
drop table if exists #mcotableNoDup;
SELECT distinct ben_id,
FIRST_VALUE(ADD_REGION) OVER (PARTITION BY BEN_ID ORDER BY valueocaddr, ADD_REGION, valueocmco, mco desc) AS ADD_REGION,
FIRST_VALUE(MCO) OVER (PARTITION BY BEN_ID ORDER BY valueocmco, mco, valueocaddr, add_region desc) AS MCO
into #mcotableNoDup
from #mcotable 
group by BEN_ID, mco, add_region, valueocmco, valueocaddr
order by ben_id;

